INSERT INTO company_evaluations (id, progress_ref_id, evaluation_date, overall_score, comments) VALUES
                                                                                                    (1, 1, DATE_SUB(NOW(), INTERVAL 3 DAY), 8.5, 'Lê Thị Anh là một thực tập sinh có năng lực và tinh thần học hỏi tốt. Em đã hoàn thành tốt các nhiệm vụ được giao trong dự án phát triển ứng dụng di động và thể hiện khả năng làm việc nhóm tốt. Em có kiến thức vững về Java và Kotlin, nhanh chóng tiếp thu các kỹ thuật mới trong phát triển ứng dụng di động. Trong quá trình thực tập, em đã đóng góp vào các tính năng quản lý người dùng và tích hợp thanh toán. Kỹ năng debug và phân tích vấn đề của em cần được cải thiện thêm, nhưng nhìn chung em đã thể hiện tiềm năng tốt và đạt được sự tiến bộ đáng kể trong thời gian thực tập.'),
                                                                                                    (2, 2, DATE_SUB(NOW(), INTERVAL 3 DAY), 9.0, 'Nguyễn Thị Trang là một thực tập sinh xuất sắc. Em có kiến thức tốt về ReactJS và các thư viện UI hiện đại. Em nhanh chóng hòa nhập với team và làm việc hiệu quả trong môi trường đa quốc gia. Các component do em phát triển đều đáp ứng yêu cầu về UI/UX và performance. Em chủ động trong việc tìm hiểu, học hỏi và đề xuất giải pháp cải thiện. Kỹ năng giao tiếp và làm việc nhóm của em rất tốt. Em hoàn thành tất cả các nhiệm vụ được giao đúng hạn và chất lượng cao. Chúng tôi đánh giá cao tinh thần làm việc của em và sẵn sàng xem xét tuyển dụng chính thức sau khi em tốt nghiệp.');
INSERT INTO company_evaluation_details (id, evaluation_id, criteria_id, score, comments) VALUES
                                                                                             (1, 1, 1, 8.0, 'Kiến thức tốt về Android development sử dụng Java và Kotlin. Có khả năng áp dụng các mẫu thiết kế (design patterns) vào các vấn đề thực tế. Hiểu và áp dụng được nguyên tắc clean code vào dự án.'),
                                                                                             (2, 1, 2, 9.0, 'Khả năng học hỏi nhanh, chủ động tìm hiểu công nghệ mới và áp dụng vào công việc. Tích cực tham gia các buổi chia sẻ kiến thức nội bộ và đặt câu hỏi sâu sắc.'),
                                                                                             (3, 1, 3, 8.5, 'Làm việc tốt với các thành viên trong team, tham gia tích cực vào các cuộc họp và đóng góp ý kiến xây dựng. Sẵn sàng hỗ trợ đồng nghiệp khi cần thiết.'),
                                                                                             (4, 1, 4, 9.0, 'Thái độ làm việc nghiêm túc, có trách nhiệm. Luôn đúng giờ và hoàn thành nhiệm vụ đúng thời hạn. Tinh thần cầu tiến và không ngại khó.'),
                                                                                             (5, 1, 5, 8.0, 'Khả năng phân tích vấn đề tốt, tuy nhiên đôi khi còn gặp khó khăn trong việc tìm ra nguyên nhân gốc rễ của lỗi phức tạp. Cần cải thiện kỹ năng debug.'),
                                                                                             (6, 1, 6, 8.5, 'Giao tiếp tốt, trình bày ý tưởng rõ ràng trong các cuộc họp và trao đổi công việc. Tài liệu hóa code tốt, giúp các thành viên khác dễ dàng hiểu và tiếp cận.'),
                                                                                             (7, 2, 1, 9.0, 'Kiến thức vững về ReactJS, Redux và các công nghệ frontend hiện đại. Có khả năng tối ưu hiệu năng của ứng dụng web và áp dụng các best practices trong phát triển frontend.'),
                                                                                             (8, 2, 2, 9.5, 'Khả năng học hỏi nhanh, tiếp thu nhanh các công nghệ và thư viện mới. Chủ động nghiên cứu và đề xuất giải pháp cải tiến cho dự án.'),
                                                                                             (9, 2, 3, 9.0, 'Làm việc nhóm xuất sắc, phối hợp tốt với các thành viên khác trong team đa quốc gia. Chủ động chia sẻ kiến thức và hỗ trợ đồng nghiệp.'),
                                                                                             (10, 2, 4, 9.0, 'Thái độ làm việc tích cực, nghiêm túc và chuyên nghiệp. Luôn sẵn sàng nhận nhiệm vụ mới và hoàn thành đúng thời hạn với chất lượng cao.'),
                                                                                             (11, 2, 5, 9.0, 'Khả năng phân tích và giải quyết vấn đề tốt, đặc biệt là trong việc xử lý các issue về UI/UX phức tạp. Có tư duy logic tốt và tìm ra các giải pháp hiệu quả.'),
                                                                                             (12, 2, 6, 9.0, 'Kỹ năng giao tiếp tốt, cả bằng tiếng Việt và tiếng Anh. Trình bày ý tưởng rõ ràng, mạch lạc và thuyết phục trong các cuộc họp và tài liệu kỹ thuật.');