(4, 3, '2024.2', 'Network Engineer Intern', 'Hỗ trợ đội ngũ kỹ thuật trong việc vận hành và phát triển hệ thống mạng', 'Sinh viên năm cuối ngành CNTT hoặc Điện tử Viễn thông, có kiến thức về mạng máy tính, TCP/IP', 'Được đào tạo về các công nghệ mạng mới nhất, tiếp xúc với hệ thống mạng quy mô lớn, trợ cấp 4 triệu/tháng', 2, 1, 'FULL_TIME', '2024-04-15', NOW(), NOW(), NULL),
(5, 4, '2024.2', 'Software Tester Intern', 'Tham gia đội ngũ kiểm thử phần mềm, phát triển và thực hiện các kịch bản kiểm thử', 'Sinh viên năm cuối ngành CNTT, có kiến thức cơ bản về quy trình phát triển phần mềm, có tư duy logic tốt', 'Được đào tạo về kiểm thử phần mềm, làm việc trong môi trường chuyên nghiệp, trợ cấp 4 triệu/tháng', 3, 1, 'FULL_TIME', '2024-04-30', NOW(), NOW(), NULL),
(6, 5, '2024.2', 'Data Engineer Intern', 'Tham gia vào việc thu thập, xử lý và phân tích dữ liệu lớn', 'Sinh viên năm cuối ngành CNTT hoặc Toán Tin, có kiến thức về SQL, Python, và các công cụ xử lý dữ liệu', 'Được làm việc với dữ liệu thực tế quy mô lớn, được hướng dẫn bởi các chuyên gia dữ liệu, trợ cấp 5.5 triệu/tháng', 2, 0, 'FULL_TIME', '2024-04-28', NOW(), NOW(), NULL),
(7, 6, '2024.2', 'Cloud Engineer Intern', 'Hỗ trợ đội ngũ kỹ thuật trong việc triển khai và quản lý các dịch vụ đám mây', 'Sinh viên năm cuối ngành CNTT, có kiến thức về cloud computing, hiểu biết cơ bản về AWS hoặc Azure', 'Được làm việc với các công nghệ đám mây hiện đại, được đào tạo về các dịch vụ Microsoft, trợ cấp 6 triệu/tháng', 2, 0, 'FULL_TIME', '2024-04-25', NOW(), NOW(), NULL),
(8, 7, '2024.2', 'Machine Learning Intern', 'Tham gia vào các dự án ứng dụng machine learning trong các sản phẩm của Google', 'Sinh viên năm cuối ngành CNTT hoặc Toán Tin, có kiến thức về machine learning, Python, và TensorFlow', 'Được làm việc với các kỹ sư hàng đầu, tiếp xúc với dữ liệu thực tế quy mô lớn, trợ cấp 7 triệu/tháng', 1, 0, 'FULL_TIME', '2024-04-20', NOW(), NOW(), NULL);
